     if(action.type === 'ADD_TO_CARD_LIST'){
         let addItem = state.products.find(pro=> pro.id===action.id)
         return{
             ... state,
             products,
             addItem:[...state, products.id]
         }
     }    
    }

    const mapStateToProps=(state)=>{
        return{
            products:state.products
        }
      }

