import { addItem} from '../actions/action-types/homeActions'


const initState = {
     items: [
        this.props.products
    ],
    addedItems:[],
    total: 0

    }

    const productsReducer= (state = initState,action)=>{

     if(action.type === 'ADD_TO_CARD_LIST'){
         let addItem = state.products.find(pro=> pro.id===action.id)
         addItem.quantity +=1
         return{
             state,
             total:state.total + addItem.price
         }
     }    
    }

    export default productsReducer
