import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,addQuantity,subtractQuantity} from '../actions/cardActions'
class Cart extends Component{


    //to remove the item completely
    handleRemove = (id)=>{
        this.props.removeItem(id);
    }
    //to add the quantity
    handleAddQuantity = (id)=>{
        this.props.addQuantity(id);
    }
    //to substruct from the quantity
    handleSubtractQuantity = (id)=>{
        this.props.subtractQuantity(id);
    }
    render(){

        console.log('Cart',this.props.items)

        let addedItems = this.props.items.length ?
            (  
                this.props.items.map(item=>{
                    return(

                        <li key={item.id}>
                                    <div > 
                                        <img src={item.img} alt={item.img}/>
                                    </div>                         
                                    <div >
                                        <span >{item.title}</span>
                                        <p>{item.desc}</p>
                                        <p><b>Price: {item.price}$</b></p> 
                                        <p>
                                            <b>Quantity: {item.quantity}</b> 
                                        </p>
                                        <div >

                                            {/* //TODO: fix this part */}
                                            <Link to="/cart"><i  onClick={()=>{this.handleAddQuantity(item.id)}}>arrow_drop_up</i></Link>
                                            <Link to="/cart"><i  onClick={()=>{this.handleSubtractQuantity(item.id)}}>arrow_drop_down</i></Link>
                                        </div>
                                        <button  onClick={()=>{this.handleRemove(item.id)}}>Remove</button>
                                    </div>                                
                                </li>                
                    )
                })
            ):

             (
                <p>Your Card is Empty</p>
             )
       return(
            <div className="container">
                <div className="cart">
                    <h5>You have ordered:</h5>
                    <ul className="collection">
                        {addedItems}
                    </ul>
                </div>      
            </div>
       )
    }
}
const mapStateToProps = (state)=>{
    return{
        items: state.card.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
