import { connect } from 'react-redux'



const initState = {
     products: [
        this.props.products
    ],
    addItem:[],

    }


    const productsReducer= (state = initState,action)=>{

     if(action.type === 'ADD_TO_CARD_LIST'){
        let products = state.products.find(pro=> pro.id===action.id)
         return{
             ... state,
             addItem:products
         }
     }    
    }


    export default connect (mapStateToProps) (productsReducer)
