import { connect } from 'react-redux'


class Home extends Component {
  render() {

    let addedProduct = this.props.products.length?
    (
    this.props.products.map(pro=>{
        return(
             <li key={pro.id}>
              <div>
                  <img src={pro.img} alt={pro.title}/>
              </div>
              <div>
                  <span>{pro.title}</span>
                  <p>{pro.desc}</p>
                  <p>Price: ${pro.price}</p>
              </div>
              <div>
                  <button onClick={()=>{this.handleAdd(pro.id)}}>Add to cart</button>
              </div>

             </li>
            )
    })
    ):''


    // console.log('Home',this.props)

    return (
      <div>
       <div>{addedProduct}</div>
      </div>
    )
  }
}

